        mHdltvoutHandle << " , " <<  " \"ap_ready\" :  \"" << ap_ready.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"alloc_size\" :  \"" << alloc_size.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"alloc_size_ap_vld\" :  \"" << alloc_size_ap_vld.read() << "\" ";
        mHdltvoutHandle << " , " <<  " \"alloc_size_ap_ack\" :  \"" << alloc_size_ap_ack.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"alloc_free_target\" :  \"" << alloc_free_target.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"alloc_free_target_ap_vld\" :  \"" << alloc_free_target_ap_vld.read() << "\" ";
        mHdltvoutHandle << " , " <<  " \"alloc_free_target_ap_ack\" :  \"" << alloc_free_target_ap_ack.read() << "\" ";
